// Switchdef a = 1def log = ''switch (a) {  // case 0  and 1 fall through  case 0 :log+='0'  case 1 :log+='1'  // case 2 breaks the control  case 2 :log+='2';break  default : log += 'default'}assert log == '12'// Switch with classfiers. Quite cool.switch (12) {  case 0: assert false; break  case 0..9: assert false; break  case [8,9,11]: assert false; break  // Can classify by type  case Float: assert false; break  // Result of the closure call is coerced to a Boolean  case {it%3 == 0}: assert true; break  case ~/../: assert false; break  default: assert false; break}