// Positional parametersclass VendorWithCtor {  String name, product  VendorWithCtor(name, product) {    this.name = name    this.product = product  }}// ￼￼￼￼Normal constructor usedef first = new VendorWithCtor('Canoo','ULC')// Coercion with 'as'def second = ['Canoo','ULC'] as VendorWithCtorassert second.name == 'Canoo'// Coercion in assignmentVendorWithCtor third = ['Canoo','ULC']assert third.product == 'ULC'// Implicit contructor with named parametersclass Vendor {  String name, product}new Vendor()new Vendor(name:'Canoo')new Vendor(product:'ULC')new Vendor(name:'Canoo', product:'ULC')def vendor = new Vendor(name:'Canoo', product:'Cool named parameters')assert 'Canoo' == vendor.nameprintln vendor.product